    ClasseBanco bd = new ClasseBanco(1, 1014, 1514, 100);
    
    private void btnSacarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSacarActionPerformed
        // TODO add your handling code here:
        int op = cbOp.getSelectedIndex();
        double saldo = Double.parseDouble(txtValor.getText());
        switch(op){
            case 0:
                bd.sacar(saldo, true);
                lblSaldo.setText(Double.toString(bd.getSaldo()));
                //lblSaldo.setText(Double.toString(bd.saldo()));
                break;
            case 1:
                bd.sacar(saldo);
                lblSaldo.setText(Double.toString(bd.getSaldo()));
                break;
        }
        
    }//GEN-LAST:event_btnSacarActionPerformed

    private void btnDepositarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDepositarActionPerformed
        // TODO add your handling code here:
        int op = cbOp.getSelectedIndex();
        double saldo = Double.parseDouble(txtValor.getText());
        switch(op){
            case 0:
                bd.depositar(saldo);
                lblSaldo.setText(Double.toString(bd.getSaldo()));
                break;
            case 1:
                bd.depositar(saldo);
                lblSaldo.setText(Double.toString(bd.getSaldo()));
                break;
        }
    }//GEN-LAST:event_btnDepositarActionPerformed

