    private void btnIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIgualActionPerformed
        // TODO add your handling code here:
        if(txtResult.getText().isEmpty()){
            txtResult.setText("");
            lblFirstNumber.setText("");
        }else {
            lblFirstNumber.setText(null);
            calculo.setN2(Double.parseDouble(txtResult.getText()));

            if("soma".equals(calculo.getOp())){
                txtResult.setText(Double.toString(calculo.somarESubtrair(calculo.getN1(), calculo.getN2())));
            }else if("subtracao".equals(calculo.getOp())){
                txtResult.setText(Float.toString(calculo.somarESubtrair((float)calculo.getN1(), (float)calculo.getN2())));
            }else if("multiplicacao".equals(calculo.getOp())){
                txtResult.setText(Double.toString(calculo.multiplicar(calculo.getN1(), calculo.getN2())));
            }else if("divisao".equals(calculo.getOp())){
                txtResult.setText(Double.toString(calculo.dividir(calculo.getN1(), calculo.getN2())));
            }

            calculo.setOp(null);   //PARA PERCENTAGEM
        }
    }//GEN-LAST:event_btnIgualActionPerformed

    private void jButton11ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton11ActionPerformed
        // INVERSÃƒO DE SINAL
        double inversao = Double.parseDouble(txtResult.getText());
        if(inversao > 0){
            txtResult.setText("-"+inversao);
        }else if(inversao < 0){
            txtResult.setText("+"+inversao);
        }
    }//GEN-LAST:event_jButton11ActionPerformed

    private void jButton20ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton20ActionPerformed
        String sinal="";
        if("soma".equals(calculo.getOp())){
            sinal = "+";
        }else if("subtracao".equals(calculo.getOp())){
            sinal = "-";
        }else if("multiplicacao".equals(calculo.getOp())){
            sinal = "*";
        }else if("divisao".equals(calculo.getOp())){
            sinal = "/";
        }
        if("soma".equals(calculo.getOp()) || "subtracao".equals(calculo.getOp()) || "multiplicacao".equals(calculo.getOp()) || "divisao".equals(calculo.getOp())){
            calculo.setN2(calculo.opPorcentagem(Double.parseDouble(txtResult.getText()), calculo.getN1()));
            lblFirstNumber.setText(calculo.getN1() + sinal + calculo.getN2());
            txtResult.setText(Double.toString(calculo.getN2()));
        }else {
            txtResult.setText("0");
        }
    }//GEN-LAST:event_jButton20ActionPerformed

